import { useEffect, useState, useRef } from 'react';
import logo from './logo.svg';
import './App.css';
import './App.css';

function App() {
  const [currentTitleIndex, setCurrentTitleIndex] = useState(0);
  const [currentCharIndex, setCurrentCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const cursorDotRef = useRef(null);
  const cursorOutlineRef = useRef(null);
  const headerRef = useRef(null);
  const navLinksRef = useRef(null);
  const mobileMenuRef = useRef(null);
  const backToTopRef = useRef(null);
  const [statsAnimated, setStatsAnimated] = useState(false);

  const titles = ["Full-Stack Developer", "AI Enthusiast", "Problem Solver", "Tech Innovator"];

  // Typewriter effect
  useEffect(() => {
    const type = () => {
      const element = document.querySelector('.hero-subtitle');
      if (!element) return;

      const currentTitle = titles[currentTitleIndex];

      if (isDeleting) {
        element.textContent = currentTitle.substring(0, currentCharIndex - 1);
        setCurrentCharIndex(prev => prev - 1);
      } else {
        element.textContent = currentTitle.substring(0, currentCharIndex + 1);
        setCurrentCharIndex(prev => prev + 1);
      }

      if (!isDeleting && currentCharIndex === currentTitle.length) {
        setIsDeleting(true);
        setTimeout(type, 2000);
      } else if (isDeleting && currentCharIndex === 0) {
        setIsDeleting(false);
        setCurrentTitleIndex((prev) => (prev + 1) % titles.length);
        setTimeout(type, 500);
      } else {
        setTimeout(type, isDeleting ? 50 : 100);
      }
    };

    type();
  }, [currentCharIndex, isDeleting, currentTitleIndex]);

  // Scroll & header effects
  useEffect(() => {
    const handleScroll = () => {
      const header = headerRef.current;
      const backToTop = backToTopRef.current;

      if (!header || !backToTop) return;

      if (window.scrollY > 100) header.classList.add('header-scrolled');
      else header.classList.remove('header-scrolled');

      if (window.scrollY > 500) backToTop.classList.add('visible');
      else backToTop.classList.remove('visible');
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Cursor effects
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (cursorDotRef.current && cursorOutlineRef.current) {
        cursorDotRef.current.style.left = `${e.clientX}px`;
        cursorDotRef.current.style.top = `${e.clientY}px`;
        cursorOutlineRef.current.style.left = `${e.clientX}px`;
        cursorOutlineRef.current.style.top = `${e.clientY}px`;
      }
    };
    document.addEventListener('mousemove', handleMouseMove);
    return () => document.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Memory leak example interval
  useEffect(() => {
    const interval = setInterval(() => {
      console.log('Memory leak running...');
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Form submit
  const submitForm = (e) => {
    e.preventDefault();
    const data = Object.fromEntries(new FormData(e.target));
    console.log('Form submitted:', data);
    alert('Thank you for your message! I will get back to you soon.');
    e.target.reset();
  };

  // Mobile menu toggle
  const toggleMobileMenu = () => {
    if (navLinksRef.current && mobileMenuRef.current) {
      navLinksRef.current.classList.toggle('active');
      mobileMenuRef.current.classList.toggle('active');
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="App">
      <div className="cursor-dot" ref={cursorDotRef}></div>
      <div className="cursor-outline" ref={cursorOutlineRef}></div>

      <header id="header" ref={headerRef} className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>Edit <code>src/App.js</code> and save to reload.</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >Learn React</a>

        <nav>
          <div id="navLinks" ref={navLinksRef} className="nav-links">
            <a href="#home" onClick={() => window.scrollToSection('home')}>Home</a>
            <a href="#about" onClick={() => window.scrollToSection('about')}>About</a>
          </div>
          <div className="mobile-menu" ref={mobileMenuRef} onClick={toggleMobileMenu}>
            ☰
          </div>
        </nav>
      </header>

      <button id="backToTop" ref={backToTopRef} onClick={scrollToTop}>↑</button>

      <section id="home">
        <h2 className="hero-subtitle"></h2>
      </section>

      <section id="about">
        <h2>About Me</h2>
        <form onSubmit={submitForm}>
          <input name="name" placeholder="Name" required />
          <input name="email" placeholder="Email" required />
          <textarea name="message" placeholder="Message" required />
          <button type="submit">Send</button>
        </form>
      </section>
    </div>
  );
}

export default App;
